CSS:
/* ESTILOS PADRÃO (NÃO MODIFICAR) */
@import url("https://service.smarthint.co/plugins/fonts/Fonts.css");
@import url('https://fonts.googleapis.com/css2?family=Anton&family=Fredoka+One&family=Montserrat:ital,wght@0,100..900;1,100..900&family=Orbitron:wght@400..900&family=Great+Vibes&family=Playfair+Display:ital,wght@0,400..900;1,400..900&display=swap');

.title {
    margin-bottom: 20px
}

.info h2 {
    min-height: 57px
}

.main-line {
    margin-bottom: 20px
}

.products {
    margin-left: 0 !important;
    margin-right: 0 !important
}

.logo {
    vertical-align: middle
}

.logo img {
    max-height: 150px;
    max-width: 100%
}

.product {
    width: 24%;
    float: left;
    margin-bottom: 10px
}

.product .product-inner {
    border: 1px solid;
    border-radius: 10px;
    margin-left: 5px;
    margin-right: 5px;
    min-height: 385px
}

.product .info {
    min-height: 50px
}

.product .name a:hover {
    text-decoration: none
}

.product .ms-vip-price-container {
    min-height: 50px
}

.product .button {
    margin-bottom: 10px
}

.image {
    width: 100%
}

body {
    background-color: transparent !important;
    text-align: center
}

.modal-body {
    margin: 15px;
    padding: 0 !important
}

.btn-lg {
    font-size: 25px !important
}

.discount-coupon {
    font-weight: bold;
    border-radius: 3px;
    color: white;
    padding: 3px;
    background-color: #f20000
}

.close {
    position: fixed;
    top: 10;
    right: 10;
    font-size: 40px;
    z-index: 99999
}

.slick-dotted.slick-slider {
    margin-top: 10px
}

.slick-slide {
    height: auto !important
}

.slick-next {
    right: -16px !important
}

.slick-prev {
    left: -16px !important
}

.image {
    display: flex;
    align-items: center;
    justify-content: center;
    height: 200px;
    width: 190px;
    margin: 0 auto
}

.modal-content .slick-prev:before {
    content: '\f104' !important;
}

.modal-content .slick-next:before {
    content: '\f105' !important;
}

.image img {
    width: auto;
    height: auto;
    max-height: 200px;
    max-width: 190px
}

.slick-prev {
    z-index: 3
}

.modal-content .slick-prev:before,
.modal-content .slick-next:before,
.modal-content .slick-dots li button:before,
.modal-content .slick-dots li.slick-active button:before {
    color: #080808
}

.modal-content button.close {
    opacity: .8
}

.modal-content button.close:hover {
    opacity: .2
}

.modal-content button.close,
.modal-content button.close:hover {
    color: #080808;
    text-shadow: 0 1px 0 #080808
}


/*  */
.modal-open .counter-box {
    text-align: center;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
}

.modal-open div#counter-box {
    display: flex;
    flex-direction: column;
    align-items: center;
}

.modal-open .btn-success {
    white-space: normal;
}

.modal-open .logo {
    max-width: 80%;
    width: 100%;
}

.modal-open .row.title {
    margin: 0 auto 20px;
    max-width: 88%;
}

.modal-open .main-line,
.modal-open .title {
    margin-inline: 0;
}

.modal-open .row-button {
    align-items: center !important;
    flex-direction: column;
    gap: 10px;
}

.modal-open .btn-i-want {
    width: 100%;
}

/* --- COLE AQUI O CSS CUSTOMIZADO SMARTHINT --- */

/* --- INÍCIO DO CSS CUSTOMIZADO SMARTHINT --- */
.modal-body {
    position: relative
}

.modal-open .modal-content {
    background-color: #111111;
    border: 3px solid #ffd700;
    font-family: 'Lato';
  	background-image: url(none);
    box-shadow: 0 0 25px rgba(255, 215, 0, .5);
    border-radius: 8px;
    min-height: 400px
}

.modal-open .overlay-title {
    font-family: 'Adelle_ExtraBoldItalic';
    color: #ffd700;
    font-size: 36px;
    text-align: center;
    font-weight: normal;
    text-transform: uppercase;
    letter-spacing: 1.5px;
    text-shadow: 0 0 8px #ffd700
}

.modal-open .overlay-message {
    font-family: 'Adelle_Reg';
    color: #ffffff;
    font-size: 18px;
    text-align: justify;
    font-weight: normal;
    margin-bottom: 15px
}

.modal-open .counter-box {
    width: 100%;
    text-align: left
}

.modal-open .counter-box span {
    color: #ffffff;
    font-family: 'Adelle_ExtraBold';
    font-weight: normal;
    text-transform: uppercase;
    font-size: 14px
}

.modal-open .flip-clock-wrapper ul li a div div.inn {
    background-color: #ffd700;
    color: #111111;
    font-size: 36px;
    text-shadow: none
}

.modal-open .flip-clock-wrapper {
    margin: 10px 0
}

.modal-open .flip-clock-wrapper .flip-clock-label {
    display: none !important
}

.modal-open .row-button {
    display: flex;
    justify-content: space-between;
    align-items: flex-end
}

.modal-open .btn-success {
    background-color: #ffd700;
    color: #111111;
    border: 2px solid #ffd700;
    font-family: 'Adelle_ExtraBold';
    font-size: 18px !important;
    font-weight: normal;
    text-transform: uppercase;
    padding: 10px 20px;
    border-radius: 5px;
    letter-spacing: 1px;
    transition: all .3s ease;
    width: auto;
    display: inline-block
}

.modal-open .btn-success:hover {
    background-color: #ffffff;
    border-color: #ffffff;
    color: #111111
}

.modal-open .close {
    position: absolute;
    top: 15px;
    right: 15px;
    color: #ffd700 !important;
    text-shadow: 0 1px 2px #000;
    opacity: .9;
    font-size: 30px
}

@media screen and (max-width:767px) {
    .modal-open .overlay-title {
        font-size: 28px
    }

    .modal-open .overlay-message {
        font-size: 16px;
        text-align: center
    }

    .modal-open .main-line {
        flex-direction: column
    }

    .modal-open #counter-box,
    .modal-open .product-data {
        text-align: center
    }

    .modal-open .product-data {
        margin-top: 20px
    }

    .modal-open .row-button {
        flex-direction: column;
        align-items: center
    }

    .modal-open .row-button .logo {
        margin-bottom: 15px
    }  
  	.modal-open .modal-content {
      	background-image: url(none)
    }
}

/* --- FIM DO CSS CUSTOMIZADO SMARTHINT --- */

